mapper.insert:
INSERT INTO test_history
(strategy_id,

    strategy_version,

test_time,
exchange_name,
symbol,
start_time,
end_time,
user_id)
VALUES
( #{strategyId}

    , #{version}

, now()
, #{exchange,jdbcType=VARCHAR}
, #{symbol,jdbcType=VARCHAR}
, #{startTime}
, #{endTime}
, #{userId}
)

mapper.update:
update test_history
   set strategy_id = #{strategyId}
      , strategy_version = #{strategyVersion}
      , exchange_name = #{exchange}
      , symbol  = #{symbol,jdbcType=VARCHAR}
      , ecs_task_id = #{ecsTaskId,jdbcType=VARCHAR}
where id = #{id}

mapper.select:
SELECT a.id
, a.code
, b.options


        FROM strategy_deploy a


        FROM strategy a


join test_history b on a.id = b.strategy_id
WHERE 1=1
and b.id = #{id}
and b.user_id = #{userId}

    and a.version = ${version}
