mapper.select:
SELECT a.id , a.strategy_id , a.version , a.exchange , a.coin_unit , a.base_unit , a.description , a.is_display , a.create_time , a.recruit_start , a.recruit_end , a.invest_start , a.invest_end , a.test_start , a.test_end , a.cash , a.cash_unit , a.name , a.user_id , a.test_monthly_return , a.test_return_pct , a.create_time , ifnull(b.invest_cash, 0) as invest_cash , c.invest_id FROM goods a LEFT JOIN (SELECT aa.goods_id , sum(aa.invest_cash) as invest_cash FROM invest_goods aa GROUP BY aa.goods_id ) b on a.id = b.goods_id LEFT JOIN (SELECT bb.goods_id , bb.id as invest_id FROM invest_goods bb WHERE bb.user_id = #{userId} GROUP BY bb.goods_id ) c on a.id = c.goods_id WHERE 1=1 AND a.exchange = #{exchange}
AND a.is_display = ${isDisplay}
AND a.recruit_start <= ${recruitStart} AND a.recruit_end >= ${recruitEnd}
AND a.invest_start <= ${investStart} AND a.invest_end >= ${investEnd}
AND a.invest_start > ${investStart}
AND a.invest_end < ${investEnd}
1 = 2
ORDER BY id DESC

mapper.update:
UPDATE goods
   SET is_display = false
 WHERE id = #{id}

UPDATE goods
SET is_display = true
WHERE id = #{id}

UPDATE goods
   SET strategy_id         = ${strategyId}
     , version             = ${version}
     , exchange            = #{exchange}
     , coin_unit           = #{coinUnit}
     , base_unit           = #{baseUnit}
     , name                = #{name}
     , description         = #{description}
     , cash                = ${cash}
     , cash_unit           = #{cashUnit}
     , recruit_start       = ${recruitStart}
     , recruit_end         = ${recruitEnd}
     , invest_start        = ${investStart}
     , invest_end          = ${investEnd}
     , test_start          = ${testStart}
     , test_end            = ${testEnd}
     , test_monthly_return = #{testMonthlyReturn}
     , test_return_pct     = #{testReturnPct}
 WHERE id = ${id}

UPDATE goods
SET test_monthly_return = #{testMonthlyReturn}
  , test_return_pct     = #{testReturnPct}
WHERE id = ${goodsId}
