mapper.select:
SELECT a.id , a.strategy_id , a.version , a.exchange , a.coin_unit , a.base_unit , a.description , a.is_display , a.create_time , a.test_start , a.test_end , a.cash , a.cash_unit , a.name , a.author_id , a.test_result , a.create_time , a.task_ecs_id , ifnull(b.invest_cash, 0) as invest_cash , c.invest_id FROM goods a LEFT JOIN (SELECT aa.goods_id , sum(aa.invest_cash) as invest_cash FROM invest_goods aa WHERE aa.is_finished = false GROUP BY aa.goods_id ) b on a.id = b.goods_id LEFT JOIN (SELECT bb.goods_id , bb.id as invest_id FROM invest_goods bb WHERE bb.user_id = #{userId} AND bb.is_finished = false GROUP BY bb.goods_id ) c on a.id = c.goods_id WHERE 1=1 AND a.exchange = #{exchange}
AND a.is_display = ${isDisplay}
ORDER BY id DESC

mapper.update:
UPDATE goods
   SET is_display = false
 WHERE id = #{id}

UPDATE goods
SET is_display = true
WHERE id = #{id}

UPDATE goods
   SET id = ${id}

        , strategy_id         = ${strategyId}


        , version             = ${version}


        , exchange            = #{exchange}


        , coin_unit           = #{coinUnit}
        , base_unit           = #{baseUnit}


        , name                = #{name}


        , description         = #{description}


        , cash                = ${cash}


        , cash_unit           = #{cashUnit}


        , cash_unit           = #{cashUnit}


        , test_start          = ${testStart}


        , test_end            = ${testEnd}


        , test_result         = #{testResult}

 WHERE id = ${id}

UPDATE goods
   SET test_result = #{testResult}
 WHERE id = ${goodsId}

UPDATE goods
   SET task_ecs_id = #{taskEcsId,jdbcType=VARCHAR}
 WHERE id = ${id}

UPDATE goods a
   SET a.change_users = true
 WHERE a.id = #{param1}

UPDATE goods
SET public_invest_id = #{publicInvestId}
WHERE id = #{id}

mapper.select:
SELECT is_executing
  FROM goods
 WHERE id = #{param1}
