mapper.insert:
INSERT INTO invest_goods (goods_id, user_id, exchange_key_id, amount)
VALUES (${goodsId},
        #{userId},
        ${exchangeKeyId},
        ${amount}
)

mapper.select:
SELECT a.id , a.strategy_id , a.version , a.exchange , a.coin , a.description , a.max_amount , a.min_amount , a.is_display , a.create_time , a.ecs_id , a.recruit_start , a.recruit_end , a.invest_start , a.invest_end , a.backtest_start , a.backtest_end , a.amount , ifnull(b.recruit_amount, 0) as recruit_amount , a.currency , a.name , a.user_id , ifnull(c.is_invest, false) as is_invest FROM goods a LEFT JOIN (SELECT aa.goods_id , sum(aa.amount) as recruit_amount FROM invest_goods aa WHERE aa.user_id != '0' GROUP BY aa.goods_id ) b on a.id = b.goods_id LEFT JOIN (SELECT bb.goods_id , true as is_invest FROM invest_goods bb WHERE bb.user_id = #{userId} GROUP BY bb.goods_id ) c on a.id = c.goods_id WHERE 1=1 AND a.exchange = #{exchange}
AND a.is_display = ${isDisplay}
AND a.recruit_start <= ${recruitStart} AND a.recruit_end >= ${recruitEnd}
AND a.invest_start <= ${investStart} AND a.invest_end >= ${investEnd}
AND a.invest_start > ${investStart}
AND a.invest_end < ${investEnd}
1 = 2
ORDER BY is_display DESC, invest_start DESC, recruit_amount DESC

mapper.update:
UPDATE goods
   SET is_display = false
 WHERE id = ${id}

UPDATE goods
SET is_display = true
WHERE id = ${id}

  UPDATE goods
     SET strategy_id    = ${strategyId}
       , version        = ${version}
       , exchange       = #{exchange}
       , coin           = #{coin}
       , description    = #{description}
       , amount         = ${amount}
       , max_amount     = ${maxAmount}
       , min_amount     = ${minAmount}
       , currency       = #{currency}
       , recruit_start  = ${recruitStart}
       , recruit_end    = ${recruitEnd}
       , invest_start   = ${investStart}
       , invest_end     = ${investEnd}
       , backtest_start = ${backtestStart}
       , backtest_end   = ${backtestEnd}
       , name           = #{name}
WHERE id = ${id}
  AND user_id = #{userId}
