mapper.insert:
INSERT INTO invoice (invest_id, init_cash, returns, payment_price, status, payment_time)
VALUES

    ( ${item.investId}
    , ${item.initCash}
    , ${item.returns}
    , ${item.paymentPrice}
    , #{item.status,jdbcType=VARCHAR}


            , convert_tz(now(), @@session.time_zone, '${standardOffset}')


            , null


    )

mapper.update:
UPDATE invoice
   SET invest_id     = ${investId}
     , init_cash     = ${initCash}
     , returns       = ${returns}
     , payment_price = ${paymentPrice}
     , status        = ${status}
