mapper.select:
SELECT ID , VERSION , NAME , USER_ID , CODE , OPTIONS , CREATE_TIME , UPDATE_TIME , WRITER FROM strategy WHERE 1=1
and ID = #{id}
and USER_ID = #{userId}
ORDER BY CREATE_TIME DESC

mapper.insert:
INSERT INTO strategy(VERSION, NAME, USER_ID, CREATE_TIME, CODE, OPTIONS, WRITER)
values( '1.0'
      , #{name}
      , #{userId}
      , now()
      , #{code}
      , #{options}
      , #{writer}
)

mapper.update:
UPDATE strategy
   SET CODE = #{code}
     , OPTIONS = #{options}
     , NAME = #{name}
     , UPDATE_TIME = now()
 WHERE id = #{id}
