mapper.select:
SELECT ID , VERSION , NAME , USER_ID , CREATE_TIME , CODE , OPTIONS FROM strategy WHERE 1=1
and ID = #{id}
and USER_ID = #{userId}

mapper.insert:
INSERT INTO strategy(ID, VERSION, NAME, USER_ID, CREATE_TIME, CODE, OPTIONS)
values( #{id}
      , #{version}
      , #{name}
      , #{userId}
      , now()
      , #{code}
      , #{options}
)

mapper.update:
UPDATE strategy
   SET CODE = #{code}
     , OPTIONS = #{options}
     , NAME = #{name}
 WHERE id = #{id}
