mapper.insert:
INSERT INTO strategy(NAME, USER_ID, CREATE_TIME, CODE)
values( #{name}
      , #{userId}
      , now()
      , #{code}
)

mapper.update:
UPDATE strategy
   SET CODE = #{code}
     , NAME = #{name}
     , UPDATE_TIME = now()
 WHERE id = #{id}
   and user_id = #{userId}

mapper.delete:
DELETE FROM strategy
WHERE id = #{id}
  and user_id = #{userId}
