mapper.select:
SELECT ID , NAME , USER_ID , CODE , OPTIONS , CREATE_TIME , UPDATE_TIME , WRITER FROM strategy WHERE 1=1
and ID = #{id}
and USER_ID = #{userId}
and 1=2
ORDER BY CREATE_TIME DESC

mapper.insert:
INSERT INTO strategy(NAME, USER_ID, CREATE_TIME, CODE, OPTIONS, WRITER)
values( #{name}
      , #{userId}
      , now()
      , #{code}
      , #{options}
      , #{userId}
)

mapper.update:
UPDATE strategy
   SET CODE = #{code}
     , OPTIONS = #{options}
     , NAME = #{name}
     , UPDATE_TIME = now()
 WHERE id = #{id}
