mapper.insert:
INSERT INTO strategy_deploy (id, version, code, description)
VALUES ( ${id}
       , IFNULL((SELECT *
                   FROM (SELECT MAX(version) + 1
                           FROM strategy_deploy
                          WHERE id = ${id}) as tmp)
               , 1)
       , #{code}
       , #{description}
)

mapper.update:
UPDATE strategy_deploy
   SET is_trash = true
 WHERE id = ${id}
   AND version = ${version}

UPDATE strategy_deploy
   SET is_trash = true
 WHERE id = ${id}
