mapper.select:
SELECT id
     , version
     , user_id
     , status
     , time
  FROM strategy_status
 WHERE id = ${id}
   AND version = ${version}
   AND user_id = #{userId}

mapper.insert:
INSERT INTO strategy_status (id, version, user_id, time, status)
VALUES ( ${id}
       , ${version}
       , #{userId}
       , now()
       , 'use'
)

mapper.update:
UPDATE strategy_status
   SET status = 'use'
     , time = now()
 WHERE id = ${id}
   AND version = ${version}
   AND user_id = #{userId}

UPDATE strategy_status
SET status = 'unused'
WHERE id = ${id}
AND version = ${version}
AND user_id = #{userId}
